// src/pages/Giving.js
import React from 'react';
import donationService from '../services/donationService';

const Giving = () => {
  const handleDonation = (amount) => {
    // This is just a placeholder for handling the donation.
    donationService.submitDonation({ amount })
      .then(() => alert('Thank you for your generosity!'))
      .catch(() => alert('There was an error processing your donation.'));
  };

  return (
    <div>
      <h1>Giving</h1>
      <p>Your generous contributions make our ministry possible.</p>
      <button onClick={() => handleDonation(50)}>Donate $50</button>
      <button onClick={() => handleDonation(100)}>Donate $100</button>
      <button onClick={() => handleDonation(200)}>Donate $200</button>
    </div>
  );
};

export default Giving;
