// Sermons.js
import React, { useContext, useEffect } from 'react';
import { SermonContext } from '../assets/context/SermonContext';
import SermonCard from '../components/SermonCard';
import Slider from 'react-slick'; // Import the Slider component from Slick

const Sermons = () => {
  const { sermons, loading, error, refresh } = useContext(SermonContext);

  useEffect(() => {
    console.log('Sermons:', sermons);
  }, [sermons]);

  if (loading) {
    return <p>Loading sermons...</p>;
  }

  if (error) {
    return (
      <div className="error-container">
        <p>{error}</p>
        <button onClick={refresh} className="retry-button">Try Again</button>
      </div>
    );
  }

  // Carousel settings
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3, // Number of cards to show at once
    slidesToScroll: 1,
    nextArrow: <div className="slick-arrow slick-next">→</div>, // Customize next arrow
    prevArrow: <div className="slick-arrow slick-prev">←</div>, // Customize prev arrow
    responsive: [
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 1, // Show 1 card on small screens
        },
      },
    ],
  };

  return (
    <div className="sermons-container">
      <header className="sermons-header">
        <h1 className="sermon-title">All Sermons</h1>
        <p className="sermon-subtitle">
          Join us in faith and wisdom through our latest sermons.
        </p>
        <button className="cta-button">Watch Latest Sermons</button>
      </header>

      <section className="sermons-list">
        {sermons && sermons.length > 0 ? (
          <Slider {...settings}>
          // Inside the return statement of your Sermons component
            {sermons.map((sermon) => (
              <SermonCard
                key={sermon.id}
                title={sermon.title}
                description={sermon.content} // Ensure this is correct
                videoUrl={sermon.videoUrl || 'https://www.youtube.com/watch?v=lbPHM-MiEUA'} // Default URL if null
                date={sermon.date_preached} // Ensure this is correct
              />
            ))}

          </Slider>
        ) : (
          <p>No sermons available at the moment. Please check back later.</p>
        )}
      </section>

      <footer className="sermons-footer">
        <p>Subscribe for the latest updates on new sermons and events.</p>
      </footer>
    </div>
  );
};

export default Sermons;
