import React, { useState } from 'react';
import './evangelistic-ministry.css'; // Import the CSS file

const EvangelisticMinistry = () => {
  const [isModalOpen, setModalOpen] = useState(false);
  const [modalImage, setModalImage] = useState(null);

  const openModal = (image) => {
    setModalImage(image);
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
    setModalImage(null);
  };

  return (
    <div className="evangelistic-ministry-container">
      {/* Hero Section */}
      <div className="evangelistic-hero-section">
        <div className="hero-overlay">
          <h1>Evangelistic Ministry</h1>
          <p>Dedicated to spreading the gospel through various outreach programs and evangelistic campaigns.</p>
        </div>
      </div>

      {/* Events Section */}
      <h2 className="evangelistic-events-title">Upcoming Evangelistic Events</h2>
      <div className="evangelistic-events-scroll-container">
        <button className="arrow-button">{"<"}</button>
        <div className="evangelistic-events-list">
          {/* Event Cards */}
          <div className="evangelistic-event-card">
            <img src="../../images/event1.jpg" alt="Event 1" />
            <h3>Community Outreach</h3>
            <p>Date: October 10, 2024</p>
            <p>Venue: Central Park</p>
          </div>
          <div className="evangelistic-event-card">
            <img src="../../images/event2.jpg" alt="Event 2" />
            <h3>Crusade for Christ</h3>
            <p>Date: November 5, 2024</p>
            <p>Venue: Main Square</p>
          </div>
          {/* Add more event cards as needed */}
        </div>
        <button className="arrow-button">{">"}</button>
      </div>

      {/* Gallery Section */}
      <h2 className="evangelistic-gallery-title">Gallery</h2>
      <div className="evangelistic-gallery">
        <div className="gallery-item" onClick={() => openModal('../../images/gallery1.jpg')}>
          <img className="gallery-thumbnail" src="../../images/gallery1.jpg" alt="Gallery 1" />
        </div>
        <div className="gallery-item" onClick={() => openModal('../../images/gallery2.jpg')}>
          <img className="gallery-thumbnail" src="../../images/gallery2.jpg" alt="Gallery 2" />
        </div>
        {/* Add more gallery items as needed */}
      </div>

      {/* Modal for Image Preview */}
      {isModalOpen && (
        <div className="modal" onClick={closeModal}>
          <span className="close" onClick={closeModal}>&times;</span>
          <img className="modal-content" src={modalImage} alt="Gallery" />
        </div>
      )}

      {/* Footer Section */}
      <footer className="evangelistic-ministry-footer">
        <p>Evangelistic Ministry © 2024. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default EvangelisticMinistry;
