// SermonCard.js
import React from 'react';
import '../assets/styles/components-css/sermon-card.css';

useEffect(() => {
  console.log('Sermons:', sermons); // Log sermons to check the data
}, [sermons]);


const SermonCard = ({ title, description, videoUrl, date }) => {
  return (
    <div className="sermon-card">
      <figure className="video-container">
        <iframe
          src={videoUrl}
          title={title}
          frameBorder="0"
          allowFullScreen
          aria-label={title} // For accessibility
        />
        <div className="overlay">
          <h3>{title}</h3>
        </div>
      </figure>
      <div className="card-content">
        <p>{description || "No description available."}</p> {/* Fallback text */}
        <span className="date">{date || "Date not available."}</span> {/* Fallback text */}
      </div>
    </div>
  );
};

export default SermonCard;
