// src/context/SermonContext.js
import React, { createContext, useState, useEffect } from 'react';
import sermonService from '../../services/sermonService'; // Import the sermon service

const SermonContext = createContext();

const SermonProvider = ({ children }) => {
  const [sermons, setSermons] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const fetchSermons = async () => {
    setLoading(true);
    setError('');

    try {
      const data = await sermonService.getSermons(); // Use the service to fetch sermons
      console.log('Fetched sermons:', data); // Log the fetched data
      setSermons(data);
    } catch (error) {
      console.error('Error fetching sermons:', error);
      setError('Error fetching sermons. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const refresh = () => {
    fetchSermons();
  };

  useEffect(() => {
    fetchSermons();
  }, []);

  return (
    <SermonContext.Provider value={{ sermons, loading, error, refresh }}>
      {children}
    </SermonContext.Provider>
  );
};

export { SermonProvider, SermonContext };
