import React, { useState } from 'react';
import axios from 'axios';
import { apiUrl } from '../apiConfig';

const AddSermonForm = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [date, setDate] = useState('');
  const [content, setContent] = useState('');  // New state for content
  const [message, setMessage] = useState('');  // For success/error messages
  const [loading, setLoading] = useState(false);  // Loading state

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);  // Start loading
    setMessage('');  // Clear previous messages

    try {
      const response = await axios.post(`${apiUrl}/sermons`, {
        title,
        description,
        date,
        content  // Include content in the request body
      });

      // Optionally, clear the form fields after successful submission
      setTitle('');
      setDescription('');
      setDate('');
      setContent('');  // Clear content

      setMessage('Sermon added successfully!');  // Success message
      console.log('Sermon added:', response.data);

    } catch (error) {
      console.error('Error adding sermon:', error);
      setMessage('Error adding sermon. Please try again.');  // Error message
    } finally {
      setLoading(false);  // Stop loading
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <textarea
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
        <input
          type="datetime-local"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
        />
        <textarea  // New textarea for content
          placeholder="Content"
          value={content}
          onChange={(e) => setContent(e.target.value)}
          required
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Adding Sermon...' : 'Add Sermon'}
        </button>
      </form>

      {/* Display success/error message */}
      {message && <p>{message}</p>}
    </div>
  );
};

export default AddSermonForm;
