import React, { createContext, useState } from 'react';

// Create the context
export const MinistryContext = createContext();

// Create a provider component
export const MinistryProvider = ({ children }) => {
  const [ministries, setMinistries] = useState([
    { id: 1, name: 'Youth Ministry', description: 'Engaging youth in faith.', image: 'path/to/image.jpg' },
    { id: 2, name: 'Women Ministry', description: 'Empowering women in faith.', image: 'path/to/image.jpg' },
    // Add more ministries as needed
  ]);

  return (
    <MinistryContext.Provider value={{ ministries }}>
      {children}
    </MinistryContext.Provider>
  );
};
