// src/pages/SermonDetails.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import sermonService from '../services/sermonService';

const SermonDetails = () => {
  const { id } = useParams();
  const [sermon, setSermon] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchSermonDetails = async () => {
      try {
        const fetchedSermon = await sermonService.getSermonById(id);
        if (fetchedSermon) {
          setSermon(fetchedSermon);
        } else {
          setError("Sermon not found.");
        }
      } catch (err) {
        console.error("Error fetching sermon details:", err);
        setError("Failed to fetch sermon details.");
      } finally {
        setLoading(false);
      }
    };

    fetchSermonDetails();
  }, [id]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h1>{sermon.title}</h1>
      <h2>Preacher: {sermon.preacher}</h2>
      <p>Date Preached: {new Date(sermon.date_preached).toLocaleDateString()}</p>
      <p>{sermon.content}</p>
      {/* You can add more UI elements as needed */}
    </div>
  );
};

export default SermonDetails;
