import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import './assets/styles/global.css'; // Import your CSS file


// Importing Context Providers
import { SermonProvider } from './assets/context/SermonContext';
import EventProvider from './assets/context/EventContext'; // Import EventProvider

// Importing pages
import Home from './pages/Home';
import About from './pages/About';
import Ministries from './pages/Ministries';
import Sermons from './pages/Sermons';
import SermonDetails from './pages/SermonDetails';
import Events from './pages/Events';
import EventDetails from './pages/EventDetails';
import LiveStream from './pages/LiveStream';
import Contact from './pages/Contact';
import Blog from './pages/Blog';
import Giving from './pages/Giving';
import VisitorInfo from './pages/VisitorInfo';

const App = () => {
  return (
    // Wrap the Router and Routes with both the SermonProvider and EventProvider
    <SermonProvider>
      <EventProvider> {/* Added EventProvider here */}
        <Router>
          <div className="App">
            {/* Header Component */}
            <Header />

            {/* Main Content - Routing Setup */}
            <main>
              <Routes>
                {/* Redirect root ("/") to Home ("/home") */}
                <Route path="/" element={<Navigate to="/home" />} />

                {/* Define the Home page */}
                <Route path="/home" element={<Home />} />

                {/* Other routes */}
                <Route path="/about" element={<About />} />
                <Route path="/ministries" element={<Ministries />} />
                <Route path="/sermons" element={<Sermons />} />
                <Route path="/sermons/:id" element={<SermonDetails />} />
                <Route path="/events" element={<Events />} />
                <Route path="/events/:id" element={<EventDetails />} />
                <Route path="/live-stream" element={<LiveStream />} />
                <Route path="/contact" element={<Contact />} />
                <Route path="/blog" element={<Blog />} />
                <Route path="/giving" element={<Giving />} />
                <Route path="/visitor-info" element={<VisitorInfo />} />

                {/* Catch-all route for undefined paths */}
                <Route path="*" element={<Navigate to="/home" />} />
              </Routes>
            </main>

            {/* Footer Component */}
            <Footer />
          </div>
        </Router>
      </EventProvider>
    </SermonProvider>
  );
};

export default App;
