import React, { useState } from 'react';
import '../assets/styles/components-css/young-adult-ministry.css'; // Updated CSS file path

const youngAdultEvents = [
  {
    id: 1,
    title: 'Career Workshop',
    date: '2024-01-25',
    description: 'A workshop focused on building career skills.',
    location: 'Youth Hall',
    imageUrl: '../../images/event1.jpg', // Placeholder image
  },
  {
    id: 2,
    title: 'Valentine\'s Fellowship',
    date: '2024-02-14',
    description: 'A special fellowship for young adults.',
    location: 'Main Auditorium',
    imageUrl: '../../images/event2.jpg', // Placeholder image
  },
  // Add more events as needed
];

const galleryImages = [
  { id: 1, imageUrl: '../../images/gallery1.jpg' },
  { id: 2, imageUrl: '../../images/gallery2.jpg' },
  // Add more gallery images as needed
];

const YoungAdultMinistry = () => {
  const [isModalOpen, setModalOpen] = useState(false);
  const [modalImage, setModalImage] = useState(null);

  const openModal = (image) => {
    setModalImage(image);
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
    setModalImage(null);
  };

  return (
    <div className="young-adult-ministry-container">
      {/* Hero Section */}
      <section className="young-adult-hero-section">
        <div className="hero-overlay">
          <h1>Young Adult Ministry</h1>
          <p>Focusing on the spiritual and professional development of young adults, helping them navigate the challenges of early adulthood and career growth.</p>
        </div>
      </section>

      {/* Events Section */}
      <h2 className="young-adult-events-title">Upcoming Events</h2>
      <div className="young-adult-events-scroll-container">
        <button className="arrow-button left">←</button>
        <section className="young-adult-events-list">
          {youngAdultEvents.map((event) => (
            <div className="young-adult-event-card" key={event.id}>
              <img src={event.imageUrl} alt={event.title} />
              <h3>{event.title}</h3>
              <p>Date: {event.date}</p>
              <p>Venue: {event.location}</p>
              <p>{event.description}</p>
            </div>
          ))}
        </section>
        <button className="arrow-button right">→</button>
      </div>

      {/* Gallery Section */}
      <h2 className="young-adult-gallery-title">Gallery</h2>
      <div className="young-adult-gallery">
        {galleryImages.map((item) => (
          <div className="gallery-item" key={item.id} onClick={() => openModal(item.imageUrl)}>
            <img className="gallery-thumbnail" src={item.imageUrl} alt={`Gallery ${item.id}`} />
          </div>
        ))}
      </div>

      {/* Modal for Image Preview */}
      {isModalOpen && (
        <div className="modal" onClick={closeModal}>
          <span className="close" onClick={closeModal}>&times;</span>
          <img className="modal-content" src={modalImage} alt="Gallery Preview" />
        </div>
      )}

      {/* Footer */}
      <footer className="young-adult-ministry-footer">
        <p>Young Adult Ministry © 2024. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default YoungAdultMinistry;
