import React, { useContext, useEffect } from 'react';
import SermonCard from '../components/SermonCard';
import { SermonContext } from '../assets/context/SermonContext';

const Sermons = () => {
  const { sermons, loading, error, refresh } = useContext(SermonContext);

  useEffect(() => {
    console.log('Sermons:', sermons); // Log sermons to check the data
  }, [sermons]);

  const handleRefresh = () => {
    refresh(); // Call refresh to get the latest sermons
  };

  if (loading) {
    return <p className="loading-text">Loading sermons...</p>; // You can style this class if needed
  }

  if (error) {
    return (
      <div className="error-container">
        <p>{error}</p>
        <button className="cta-button" onClick={handleRefresh}>Try Again</button> {/* Use global button style */}
      </div>
    );
  }

  return (
    <div className="content-section"> {/* Added content-section for consistent styling */}
      <h1 className="sermon-title">All Sermons</h1> {/* You can style this class in global CSS */}
      <section className="sermons-list">
        {sermons && sermons.length > 0 ? (
          sermons.map(sermon => (
            <SermonCard key={sermon.id} {...sermon} />
          ))
        ) : (
          <p>No sermons available at the moment. Please check back later.</p>
        )}
      </section>
    </div>
  );
};

export default Sermons;
