// src/pages/Events.js
import React, { useContext, useEffect } from 'react';
import EventCard from '../components/EventCard';
import { EventContext } from '../assets/context/EventContext';
import '../assets/styles/components-css/events.css';

const Events = () => {
  const { events, loading, error, refresh } = useContext(EventContext); // Destructure refresh to handle retry
  useEffect(() => {
    console.log('Events:', events); // Log events to check the data
  }, [events]);

  if (loading) {
    return (
      <div className="loading-container">
        <p>Loading events...</p>
      </div> // Show loading message while fetching events
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <p className="error-message">{error}</p>
        <button onClick={refresh} className="retry-button">Try Again</button> {/* Add retry functionality */}
      </div>
    );
  }

  return (
    <div className="events-container">
      <header className="events-header">
        <h1 className="events-title">Upcoming Events</h1>
        <p className="events-subtitle">Join us for our upcoming events and activities.</p>
      </header>

      <section className="events-list">
        {events && events.length > 0 ? (
          events.map(event => (
            <EventCard key={event.id} {...event} />
          ))
        ) : (
          <div className="no-events-message">
            <p>No events available at the moment. Please check back later.</p>
          </div>
        )}
      </section>

      <footer className="events-footer">
        <p>Subscribe for the latest updates on new events and activities.</p>
      </footer>
    </div>
  );
};

export default Events;
