import React, { useContext } from 'react';
import HeroBanner from '../components/HeroBanner'; // HeroBanner will contain the static image
import SermonCard from '../components/SermonCard';
import { SermonContext } from '../assets/context/SermonContext';
import Slider from "react-slick"; // Importing the carousel library

const Home = () => {
  const { sermons } = useContext(SermonContext);

  // Carousel settings
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3, // Number of cards to show
    slidesToScroll: 1,
    arrows: true,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
        }
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        }
      }
    ]
  };

  return (
    <>
      {/* This section will have the fixed background image */}
      <div className="hero-section">
        <HeroBanner />
        <div className="hero-overlay">
          <h1>Welcome to Our Church</h1>
          <p>Join us for uplifting sermons and community events.</p>
          <button className="cta-button">Get Involved</button>
        </div>
      </div>

      {/* This is the content that will scroll below the image */}
      <div className="content-section latest-sermons"> {/* Added latest-sermons class */}
        <h2>Latest Sermons</h2>

        {/* Carousel for Latest Sermons */}
        <Slider {...settings}>
          {sermons.map((sermon) => (
            <div key={sermon.id} className="sermon-card">
              <a
                href={sermon.externalLink}
                target="_blank"
                rel="noopener noreferrer"
                style={{ textDecoration: 'none', color: 'inherit' }}
              >
                <SermonCard {...sermon} />
              </a>
            </div>
          ))}
        </Slider>

        {/* Learn More Section */}
        <div className="learn-more-section">
          <button className="learn-more-btn">Learn More</button>
        </div>
      </div>
    </>
  );
};

export default Home;
