import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Change this line
import '../assets/styles/components-css/addEvent.css'; // Import your CSS styles

const AddEvent = () => {
  const [title, setTitle] = useState('');
  const [date, setDate] = useState('');
  const [venue, setVenue] = useState('');
  const [time, setTime] = useState('');
  const { addEvent } = useContext(EventContext);
  const navigate = useNavigate(); // Change this line

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add your event logic here
    addEvent({ title, date, venue, time });

    // Navigate to the events page after adding the event
    navigate('/events'); // Change this line
  };

  return (
    <div className="add-event-container">
      <h1>Add New Event</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
        </label>
        <label>
          Date:
          <input type="date" value={date} onChange={(e) => setDate(e.target.value)} required />
        </label>
        <label>
          Venue:
          <input type="text" value={venue} onChange={(e) => setVenue(e.target.value)} required />
        </label>
        <label>
          Time:
          <input type="time" value={time} onChange={(e) => setTime(e.target.value)} required />
        </label>
        <button type="submit">Add Event</button>
      </form>
    </div>
  );
};

export default AddEvent;
