import React, { useContext } from 'react';
import HeroBanner from '../components/HeroBanner';
import SermonCard from '../components/SermonCard';
import { SermonContext } from '../assets/context/SermonContext';

const Home = () => {
  const { sermons } = useContext(SermonContext);

  return (
    <div className="home-container">
      {/* Hero section */}
      <HeroBanner />

      {/* Welcome section */}
      <section className="welcome-section">
        <h1>Welcome to Our Church</h1>
        <p>Join us as we grow in faith together. Explore our latest sermons and upcoming events!</p>
        <button className="learn-more-button">Learn More</button>
      </section>

      {/* Latest Sermons section */}
      <section className="latest-sermons-section">
        <h2>Latest Sermons</h2>
        <div className="sermons-list">
          {sermons.map((sermon) => (
            <a
              key={sermon.id}
              href={sermon.externalLink} // Make sure each sermon has an `externalLink` property
              target="_blank" // Opens the link in a new tab
              rel="noopener noreferrer" // Prevents security issues with target="_blank"
              style={{ textDecoration: 'none', color: 'inherit' }} // Remove default link styles
            >
              <SermonCard {...sermon} />
            </a>
          ))}
        </div>
      </section>
    </div>
  );
};

export default Home;
