// src/components/AddSermonForm.js
import React, { useState } from 'react';
import axios from 'axios';
import { apiUrl } from '../apiConfig';

const AddSermonForm = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [date, setDate] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`${apiUrl}/sermons`, {
        title,
        description,
        date
      });
      console.log('Sermon added:', response.data);
      // Optionally, clear the form or update the UI
    } catch (error) {
      console.error('Error adding sermon:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        required
      />
      <textarea
        placeholder="Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        required
      />
      <input
        type="datetime-local"
        value={date}
        onChange={(e) => setDate(e.target.value)}
        required
      />
      <button type="submit">Add Sermon</button>
    </form>
  );
};

export default AddSermonForm;
