import React, { useContext } from 'react';
import HeroBanner from '../components/HeroBanner';
import SermonCard from '../components/SermonCard';
import { SermonContext } from '../assets/context/SermonContext';
import ChurchImage from '../assets/DLCM.png'; // Ensure the path is correct

const Home = () => {
  const { sermons } = useContext(SermonContext);

  return (
    <div
      className="home-container"
      style={{
        backgroundImage: `url(${ChurchImage})`,
        backgroundSize: 'cover', // Make the image cover the entire container
        backgroundPosition: 'center', // Center the image
        backgroundRepeat: 'no-repeat', // Prevent the image from repeating
        height: '100vh', // Full height of the viewport
        width: '100vw', // Full width of the viewport
        overflow: 'hidden', // Prevent any overflow issues
      }}
    >
      <HeroBanner />
      <section
        className="welcome-section"
        style={{
          backgroundColor: 'rgba(255, 255, 255, 0.8)',
          padding: '20px',
          borderRadius: '8px',
        }}
      >
        <h1>Welcome to Our Church</h1>
        <p>Join us as we grow in faith together. Explore our latest sermons and upcoming events!</p>
      </section>
      <h2>Latest Sermons</h2>
      <div
        className="sermons-list"
        style={{
          backgroundColor: 'rgba(255, 255, 255, 0.8)',
          padding: '20px',
          borderRadius: '8px',
        }}
      >
        {sermons.map((sermon) => (
          <SermonCard key={sermon.id} {...sermon} />
        ))}
      </div>
    </div>
  );
};

export default Home;
