// Sermons.js
import React, { useContext, useEffect } from 'react';
import { SermonContext } from '../assets/context/SermonContext';
import SermonCard from '../components/SermonCard';

const Sermons = () => {
  const { sermons, loading, error, refresh, noSermons } = useContext(SermonContext);

  useEffect(() => {
    console.log('Sermons:', sermons); // Log sermons to check the data
  }, [sermons]);

  if (loading) {
    return <p>Loading sermons...</p>;  // Show loading text while fetching sermons
  }

  if (error) {
    return (
      <div className="error-container">
        <p>{error}</p>
        <button onClick={refresh} className="retry-button">Try Again</button>  {/* Retry fetching sermons */}
      </div>
    );
  }

  const renderSermonCards = () => {
    return sermons.map((sermon) => (
      <SermonCard
        key={sermon.id}
        title={sermon.title}
        description={sermon.content} // Assuming content is the sermon description
        preacher={sermon.preacher}
        date={sermon.date_preached}
        videoUrl={sermon.videoUrl} // Ensure this field exists
      />
    ));
  };

  return (
    <div className="sermons-container">
      <header className="sermons-header">
        <h1 className="sermon-title">All Sermons</h1>
        <p className="sermon-subtitle">
          Join us in faith and wisdom through our latest sermons.
        </p>
        <button className="cta-button">Watch Latest Sermons</button>
      </header>

      <section className="sermons-list">
        {noSermons ? (
          <p>No sermons available at the moment. Please check back later.</p>
        ) : (
          renderSermonCards()
        )}
      </section>

      <footer className="sermons-footer">
        <p>Subscribe for the latest updates on new sermons and events.</p>
      </footer>
    </div>
  );
};

export default Sermons;
