import React, { useContext, useEffect } from 'react';
import SermonCard from '../components/SermonCard';
import { SermonContext } from '../assets/context/SermonContext';

const Sermons = () => {
  const { sermons, loading, error, refresh } = useContext(SermonContext);

  useEffect(() => {
    console.log('Sermons:', sermons); // Log sermons to check the data
  }, [sermons]);

  const handleRefresh = () => {
    refresh(); // Call refresh to get the latest sermons
  };

  if (loading) {
    return <p className="loading-text">Loading sermons...</p>; // You can style this class if needed
  }

  if (error) {
    return (
      <div className="error-container">
        <p>{error}</p>
        <button className="cta-button" onClick={handleRefresh}>Try Again</button> {/* Use global button style */}
      </div>
    );
  }

  return (
    <div className="sermons-container"> {/* Wrapper for consistent styling */}
      <header className="sermons-header">
        <h1 className="sermon-title">All Sermons</h1> {/* Style this in global CSS */}
        <p className="sermon-subtitle">Join us in faith and wisdom through our latest sermons.</p> {/* Subtitle for encouragement */}
        <button className="cta-button">Watch Latest Sermons</button> {/* Prominent call to action */}
      </header>
      
      <section className="sermons-list"> {/* Flexbox or grid layout can be applied here */}
        {sermons && sermons.length > 0 ? (
          <div className="sermon-carousel"> {/* Carousel container for sermons */}
            {sermons.map(sermon => (
              <SermonCard key={sermon.id} {...sermon} />
            ))}
          </div>
        ) : (
          <p>No sermons available at the moment. Please check back later.</p>
        )}
      </section>

      <footer className="sermons-footer">
        <p>Subscribe for the latest updates on new sermons and events.</p>
        {/* You can add a subscription form or links here */}
      </footer>
    </div>
  );
};

export default Sermons;
