import axios from 'axios';
import { apiUrl } from '../apiConfig';

const eventService = {
  getEvents: async () => {
    try {
      // Retrieve token from localStorage/sessionStorage
      const token = localStorage.getItem('token'); 
      const response = await axios.get(`${apiUrl}/events`, {
        headers: {
          Authorization: `Bearer ${token}`, // Set the Authorization header
        },
      });
      return response.data; // Return the fetched data
    } catch (error) {
      console.error('Error fetching events:', error);
      return []; // Return an empty array in case of an error
    }
  },

  addEvent: async (eventData) => {
    try {
      // Retrieve token from localStorage/sessionStorage
      const token = localStorage.getItem('token');
      const response = await axios.post(`${apiUrl}/events`, eventData, {
        headers: {
          Authorization: `Bearer ${token}`, // Set the Authorization header
        },
      });
      return response.data; // Return the created event data
    } catch (error) {
      console.error('Error adding event:', error);
      throw error; // Throw error to handle in component if needed
    }
  },
};

export default eventService;
