import React, { useContext, useEffect, useRef, useState } from 'react';
import EventCard from '../components/EventCard';
import { EventContext } from '../assets/context/EventContext';
import '../assets/styles/components-css/events.css';

const Events = () => {
  const { events, loading, error, refresh } = useContext(EventContext);
  const [visibleEvents, setVisibleEvents] = useState([]);
  const [page, setPage] = useState(1);
  const observer = useRef(null);

  const EVENTS_PER_PAGE = 5; // Adjust this number based on your needs

  useEffect(() => {
    if (events.length > 0) {
      const loadEvents = events.slice(0, page * EVENTS_PER_PAGE);
      setVisibleEvents(loadEvents);
    }
  }, [events, page]);

  const lastEventElementRef = (node) => {
    if (loading) return;

    if (observer.current) observer.current.disconnect();
    observer.current = new IntersectionObserver((entries) => {
      if (entries[0].isIntersecting && visibleEvents.length < events.length) {
        setPage((prevPage) => prevPage + 1);
      }
    });

    if (node) observer.current.observe(node);
  };

  if (loading) {
    return (
      <div className="loading-container">
        <p>Loading events...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <p className="error-message">{error}</p>
        <button onClick={refresh} className="retry-button">Try Again</button>
      </div>
    );
  }

  return (
    <div className="events-container">
      <header className="events-header">
        <h1 className="events-title">Upcoming Events</h1>
        <p className="events-subtitle">Join us for our upcoming events and activities.</p>
      </header>

      {/* Make this container scrollable */}
      <section className="events-list" style={{ maxHeight: 'calc(100vh - 200px)', overflowY: 'auto' }}>
        {visibleEvents && visibleEvents.length > 0 ? (
          visibleEvents.map((event, index) => (
            <EventCard
              ref={visibleEvents.length === index + 1 ? lastEventElementRef : null} 
              key={event.id}
              {...event}
            />
          ))
        ) : (
          <div className="no-events-message">
            <p>No events available at the moment. Please check back later.</p>
          </div>
        )}
      </section>

      <footer className="events-footer">
        <p>Subscribe for the latest updates on new events and activities.</p>
      </footer>
    </div>
  );
};

export default Events;
