// src/services/sermonService.js
import axios from 'axios';
import { apiUrl } from '../apiConfig';

const getSermons = async () => {
  try {
    const response = await axios.get(`${apiUrl}/sermons`);
    return response.data;
  } catch (error) {
    console.error('Error fetching sermons:', error);
    return [];
  }
};

const getSermonById = async (id) => {
  try {
    const response = await axios.get(`${apiUrl}/sermons/${id}`);
    return response.data;
  } catch (error) {
    console.error(`Error fetching sermon with ID ${id}:`, error);
    return null;
  }
};

export default { getSermons, getSermonById };
