import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';
import { apiUrl } from '../../apiConfig';  // Ensure this URL points to your backend API

// Create the Sermon Context
const SermonContext = createContext();

// Create the Provider
const SermonProvider = ({ children }) => {
  const [sermons, setSermons] = useState([]);
  const [loading, setLoading] = useState(true);  // State to indicate loading
  const [error, setError] = useState('');  // State to capture errors

  // Function to fetch sermons data from the backend
  const fetchSermons = async () => {
    setLoading(true);  // Start loading
    setError('');  // Clear any previous errors

    try {
      const response = await axios.get(`${apiUrl}/sermons`);  // Fetch sermons from backend API

      // Ensure response is OK
      if (response.status !== 200) {
        throw new Error(`Failed to fetch sermons: ${response.statusText}`);
      }

      setSermons(response.data);  // Set the sermons data
    } catch (error) {
      console.error('Error fetching sermons:', error);
      setError('Error fetching sermons. Please try again.');
    } finally {
      setLoading(false);  // End loading
    }
  };

  // Refresh function to refetch sermons, can be called externally
  const refresh = () => {
    fetchSermons();  // Fetch sermons again
  };

  // useEffect to fetch sermons on mount
  useEffect(() => {
    fetchSermons();  // Fetch sermons when the component mounts
  }, []);

  return (
    <SermonContext.Provider value={{ sermons, loading, error, refresh }}>
      {children}  {/* This allows children components to access the context */}
    </SermonContext.Provider>
  );
};

export { SermonProvider, SermonContext };
