import React, { createContext, useState, useEffect } from 'react';

// Create the Sermon Context
const SermonContext = createContext();

// Create the Provider
const SermonProvider = ({ children }) => {
  const [sermons, setSermons] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch sermons data from an API or service
    const fetchSermons = async () => {
      try {
        const response = await fetch('/api/sermons'); // Replace with actual API endpoint

        // Check if the response is valid
        if (!response.ok) {
          throw new Error(`Error: ${response.status} - ${response.statusText}`);
        }

        // Check if the content type is JSON
        const contentType = response.headers.get('content-type');
        if (contentType && contentType.indexOf('application/json') !== -1) {
          const data = await response.json();
          setSermons(data);  // Set sermons in state
        } else {
          throw new Error('Response is not valid JSON');
        }
      } catch (error) {
        console.error('Error fetching sermons:', error);
        setError(error.message);  // Set error message
      } finally {
        setLoading(false);  // Set loading to false once fetching is done
      }
    };

    fetchSermons();
  }, []);

  return (
    <SermonContext.Provider value={{ sermons, loading, error }}>
      {children} {/* This allows children components to access the context */}
    </SermonContext.Provider>
  );
};

export { SermonProvider, SermonContext };
