// src/pages/Sermons.js
import React, { useContext, useEffect, useState } from 'react';
import { SermonContext } from '../assets/context/SermonContext';

const Sermons = () => {
  const { sermons, loading, error, refresh } = useContext(SermonContext);
  const [selectedSermon, setSelectedSermon] = useState('');

  useEffect(() => {
    console.log('Sermons:', sermons); // Log sermons to check the data
  }, [sermons]);

  const handleRefresh = () => {
    refresh(); // Call refresh to get the latest sermons
  };

  const handleChange = (e) => {
    setSelectedSermon(e.target.value); // Update selected sermon
  };

  if (loading) {
    return <p>Loading sermons...</p>;
  }

  if (error) {
    return (
      <div>
        <p>{error}</p>
        <button onClick={handleRefresh}>Try Again</button>
      </div>
    );
  }

  return (
    <div>
      <h1>All Sermons</h1>
      <select onChange={handleChange} value={selectedSermon}>
        <option value="">Select a sermon</option>
        {sermons && sermons.length > 0 ? (
          sermons.map(sermon => (
            <option key={sermon.id} value={sermon.id}>
              {sermon.title}
            </option>
          ))
        ) : (
          <option value="">No sermons available</option>
        )}
      </select>

      {/* Display selected sermon details */}
      {selectedSermon && (
        <div>
          <h2>Selected Sermon Details</h2>
          {sermons.find(sermon => sermon.id === selectedSermon) ? (
            <div>
              <h3>{sermons.find(sermon => sermon.id === selectedSermon).title}</h3>
              <p>{sermons.find(sermon => sermon.id === selectedSermon).description}</p>
              <p>Date: {sermons.find(sermon => sermon.id === selectedSermon).date}</p>
              <p>Content: {sermons.find(sermon => sermon.id === selectedSermon).content}</p>
            </div>
          ) : (
            <p>Sermon not found.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default Sermons;
