import React, { useState, useContext } from 'react';
import { AuthContext } from '../assets/context/AuthContext'; // Assuming AuthContext is correctly set up
import '../assets/styles/components-css/login.css'; // Import CSS styles for the login page

const Login = () => {
  const { login } = useContext(AuthContext); // Use login function from AuthContext
  const [credentials, setCredentials] = useState({ username: '', password: '' });
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false); // Manage loading state

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials({ ...credentials, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError(''); // Clear any previous error message

    try {
      // Make a POST request to the Flask backend
      const response = await fetch('/api/auth/login', { // Adjust the endpoint as necessary
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(credentials),
      });

      if (!response.ok) {
        // Handle errors based on the response status
        const errorData = await response.json();
        throw new Error(errorData.error || 'Login failed. Please try again.');
      }

      // Extract the token from the response
      const { token } = await response.json();

      // Save the token to local storage or context
      localStorage.setItem('token', token); // Or handle with your context
      await login(credentials.username, credentials.password); // If your context manages login state

    } catch (err) {
      setError(err.message); // Display error message
    } finally {
      setIsLoading(false); // Stop loading state
    }
  };

  return (
    <div className="login-container">
      <h1>Login</h1>
      {error && <p className="error-message">{error}</p>} {/* Display error message if exists */}
      <form onSubmit={handleSubmit} className="login-form">
        <div className="form-group">
          <label htmlFor="username">Username</label>
          <input
            type="text"
            id="username"
            name="username"
            value={credentials.username}
            onChange={handleChange}
            required
            disabled={isLoading} // Disable input during loading
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            name="password"
            value={credentials.password}
            onChange={handleChange}
            required
            disabled={isLoading} // Disable input during loading
          />
        </div>
        <button
          type="submit"
          className="login-button"
          disabled={isLoading} // Disable the button when loading
        >
          {isLoading ? 'Logging in...' : 'Login'} {/* Update button text during loading */}
        </button>
      </form>
    </div>
  );
};

export default Login;
