// src/context/EventContext.js
import React, { createContext, useState, useEffect } from 'react';
import eventService from '../../services/eventService';

export const EventContext = createContext();

const EventProvider = ({ children }) => {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true); // Added loading state
  const [error, setError] = useState(null); // Added error state

  useEffect(() => {
    const fetchEvents = async () => {
      setLoading(true); // Start loading
      try {
        const data = await eventService.getEvents(); // Fetch events from service
        setEvents(data); // Update state with fetched events
      } catch (err) {
        console.error('Error fetching events:', err);
        setError('Failed to load events. Please try again later.'); // Set error message
      } finally {
        setLoading(false); // Stop loading
      }
    };

    fetchEvents(); // Call the fetch function on component mount
  }, []);

  return (
    <EventContext.Provider value={{ events, loading, error }}>
      {children}
    </EventContext.Provider>
  );
};

export default EventProvider;
