// SermonCard.js
import React from 'react';
import '../assets/styles/components-css/sermon-card.css';

const SermonCard = ({ title, description, videoUrl, date }) => {
  const defaultVideoUrl = "https://www.youtube.com/embed/lbPHM-MiEUA"; // Default video URL

  return (
    <div className="sermon-card">
      <figure className="video-container">
        <iframe
          src={videoUrl || defaultVideoUrl}
          title={title}
          frameBorder="0"
          allowFullScreen
          aria-label={title} // For accessibility
        />
        <div className="overlay">
          <h3>{title}</h3>
        </div>
      </figure>
      <div className="card-content">
        <p>{description}</p>
        <span className="date">{date}</span>
      </div>
    </div>
  );
};

export default SermonCard;
