// src/components/SermonCard.js
import React from 'react';
import '../assets/styles/components-css/sermon-card.css';

const SermonCard = ({ title, content, date }) => {
  const videoId = "QOqoY59knds?si=hC6tBkdYWe8j7j6T"; // Replace with dynamic video ID if available

  return (
    <div className="sermon-card">
      <figure className="video-container">
        <iframe
          src={`https://www.youtube.com/embed/${videoId}`} // Use the actual video ID
          title={title}
          frameBorder="0"
          allowFullScreen
          aria-label={title}
        />
        <div className="overlay">
          <h3>{title}</h3>
        </div>
      </figure>
      <div className="card-content">
        <p>{content || "No description available."}</p> {/* Display content */}
        <p>{date}</p>
        <span className="date">{new Date(date).toLocaleDateString() || "Date not available."}</span> {/* Format date */}
      </div>
    </div>
  );
};

export default SermonCard;
