import React, { useContext } from 'react';
import SermonCard from '../components/SermonCard';
import { SermonContext } from '../assets/context/SermonContext';

const Sermons = () => {
  const { sermons, loading } = useContext(SermonContext) || {};  // Ensure we handle loading state too

  if (loading) {
    return <p>Loading sermons...</p>;
  }

  return (
    <div>
      <h1>All Sermons</h1>
      {sermons && sermons.length > 0 ? (
        sermons.map(sermon => (
          <SermonCard key={sermon.id} {...sermon} />
        ))
      ) : (
        <p>No sermons available at the moment. Please check back later.</p>
      )}
    </div>
  );
};

export default Sermons;
