import React, { useContext, useEffect, useState } from 'react';
import EventCard from '../components/EventCard';
import { EventContext } from '../assets/context/EventContext';
import axios from 'axios';
import { apiUrl } from '../apiConfig'; // Ensure this file has the correct API URL
import '../assets/styles/components-css/events.css'; // Import CSS for styling

const Events = () => {
  const { events, setEvents } = useContext(EventContext); // Destructure setEvents from context
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await axios.get(`${apiUrl}/events`); // Fetch events from the API
        setEvents(response.data); // Update the context with fetched events
      } catch (err) {
        setError('Failed to fetch events');
      } finally {
        setLoading(false); // Stop loading
      }
    };

    fetchEvents(); // Call the fetch function on component mount
  }, [setEvents]);

  if (loading) return <div>Loading...</div>; // Show loading state
  if (error) return <div>{error}</div>; // Show error state

  return (
    <div className="events-container">
      <header className="events-header">
        <h1 className="events-title">Upcoming Events</h1>
        <p className="events-subtitle">Join us for our upcoming events and activities.</p>
      </header>

      <section className="events-list">
        {events && events.length > 0 ? (
          events.map(event => (
            <EventCard key={event.id} {...event} />
          ))
        ) : (
          <div className="no-events-message">
            <p>No events available at the moment. Please check back later.</p>
          </div>
        )}
      </section>

      <footer className="events-footer">
        <p>Subscribe for the latest updates on new events and activities.</p>
      </footer>
    </div>
  );
};

export default Events;
