// SermonCard.js
import React from 'react';
import PropTypes from 'prop-types';


const SermonCard = ({ title, preacher, date_preached, content }) => {
  return (
    <div className="sermon-card">
      <h2>{title}</h2>
      <p><strong>Preacher:</strong> {preacher}</p>
      <p><strong>Date Preached:</strong> {new Date(date_preached).toLocaleDateString()}</p>
      <p>{content}</p>
    </div>
  );
};

// PropTypes validation for ensuring proper data types
SermonCard.propTypes = {
  title: PropTypes.string.isRequired,
  preacher: PropTypes.string.isRequired,
  date_preached: PropTypes.string.isRequired, // ISO date string
  content: PropTypes.string.isRequired,
};

export default SermonCard;
