// src/pages/Home.js
import React, { useContext } from 'react';
import HeroBanner from '../components/HeroBanner';
import SermonCard from '../components/SermonCard';
import { SermonContext } from '../assets/context/SermonContext';
import ChurchImage from '../assets/DLCM.png'; // Import the DLCM image

const Home = () => {
  const { sermons } = useContext(SermonContext);

  return (
    <div className="home-container" style={{ padding: '20px', maxWidth: '1000px', margin: 'auto' }}>
      {/* Display the image at the top of the page */}
      <div style={{ textAlign: 'center', marginBottom: '20px' }}>
        <img 
          src={ChurchImage} 
          alt="Church Logo" 
          style={{ width: '100%', height: 'auto', maxWidth: '600px' }} 
        />
      </div>

      <HeroBanner />
      
      <section className="welcome-section">
        <h1>Welcome to Our Church</h1>
        <p>Join us as we grow in faith together. Explore our latest sermons and upcoming events!</p>
      </section>
      
      <h2>Latest Sermons</h2>
      <div className="sermons-list">
        {sermons.map(sermon => (
          <SermonCard key={sermon.id} {...sermon} />
        ))}
      </div>
    </div>
  );
};

export default Home;
