// src/pages/Events.js
import React, { useContext, useEffect } from 'react';
import EventCard from '../components/EventCard';
import { EventContext } from '../assets/context/EventContext';
import '../assets/styles/components-css/events.css'; // Add a dedicated CSS file for styling

const Events = () => {
  const { events, loading, error, refresh } = useContext(EventContext);

  useEffect(() => {
    console.log('Events:', events); // Log events to check the data
  }, [events]);

  return (
    <div className="events-container">
      <header className="events-header">
        <h1 className="events-title">Upcoming Events</h1>
        <p className="events-subtitle">Join us for our latest gatherings and activities.</p>
      </header>

      {loading && <div className="loading-container"><p>Loading events...</p></div>} {/* Loading state */}
      {error && (
        <div className="error-container">
          <p className="error-message">{error}</p>
          <button onClick={refresh} className="retry-button">Try Again</button>
        </div> // Error handling
      )}

      <section className="events-list">
        {events && events.length > 0 ? (
          events.map(event => (
            <EventCard key={event.id} {...event} />
          ))
        ) : (
          <p className="no-events-message">No events available at the moment. Please check back later.</p>
        )}
      </section>

      <footer className="events-footer">
        <p>Subscribe for updates on upcoming events and gatherings.</p>
      </footer>
    </div>
  );
};

export default Events;
