// src/pages/Home.js
import React, { useContext } from 'react';
import HeroBanner from '../components/HeroBanner';
import SermonCard from '../components/SermonCard';
import { SermonContext } from '../assets/context/SermonContext';
import ChurchImage from '../../assets/DLCM.png'; // Ensure this path is correct

const Home = () => {
  const { sermons } = useContext(SermonContext);

  return (
    <div
      className="home-container"
      style={{
        padding: '20px',
        maxWidth: '1000px',
        margin: 'auto',
        backgroundImage: `url(${ChurchImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        minHeight: '100vh',
      }}
    >
      <HeroBanner />
      <section
        className="welcome-section"
        style={{ backgroundColor: 'rgba(255, 255, 255, 0.8)', padding: '20px' }}
      >
        <h1>Welcome to Our Church</h1>
        <p>Join us as we grow in faith together. Explore our latest sermons and upcoming events!</p>
      </section>
      <h2>Latest Sermons</h2>
      <div className="sermons-list" style={{ backgroundColor: 'rgba(255, 255, 255, 0.8)', padding: '20px' }}>
        {sermons.map((sermon) => (
          <SermonCard key={sermon.id} {...sermon} />
        ))}
      </div>
    </div>
  );
};

export default Home;
