import React from 'react';
import '../assets/styles/components-css/event-card.css'; // Assuming you have a separate CSS file for event cards

const EventCard = ({ title, date, description, location }) => {
  return (
    <div className="event-card">
      {/* You can add an image here, if available */}
      {/* <img src={eventImage} alt={title} /> */}
      <div className="event-card-content">
        <h2 className="event-card-title">{title}</h2>
        <p className="event-card-date">{new Date(date).toLocaleDateString()}</p>
        <p className="event-card-description">{description}</p>
        <p className="event-card-location">{location}</p>
      </div>
      <div className="event-card-footer">
        <button>View Details</button>
      </div>
    </div>
  );
};

export default EventCard;
