// src/pages/Sermons.js
import React, { useContext } from 'react';
import SermonCard from '../components/SermonCard';
import { SermonContext } from '../assets/context/SermonContext';

const Sermons = () => {
  const { sermons } = useContext(SermonContext) || {}; // Ensure sermons is an object to avoid errors

  return (
    <div>
      <h1>All Sermons</h1>
      {sermons && sermons.length > 0 ? (
        sermons.map(sermon => (
          <SermonCard key={sermon.id} {...sermon} />
        ))
      ) : (
        <p>No sermons available at the moment. Please check back later.</p> // Fallback for no sermons
      )}
    </div>
  );
};

export default Sermons;
