import React from 'react';
import donationService from '../services/donationService';
import '../assets/styles/components-css/giving.css'; // Import your CSS for footer styles

const Giving = () => {
  const handleDonation = (amount) => {
    // This is just a placeholder for handling the donation.
    donationService.submitDonation({ amount })
      .then(() => alert('Thank you for your generosity!'))
      .catch(() => alert('There was an error processing your donation.'));
  };

  return (
    <div className="giving-container">
      <h1>Giving</h1>
      <p>Your generous contributions make our ministry possible.</p>

      {/* Donation buttons */}
      <div className="donation-buttons">
        <button onClick={() => handleDonation(50)}>Donate $50</button>
        <button onClick={() => handleDonation(100)}>Donate $100</button>
        <button onClick={() => handleDonation(200)}>Donate $200</button>
      </div>

      {/* GCK Giving Section */}
      <section className="giving-section">
        <h2>GCK Giving</h2>
        <p>Thank you for supporting GCK. Your donations help us in our mission to spread the Gospel.</p>
        <p><strong>Bank Account Details:</strong></p>
        <p>Account Name: GCK Ministry</p>
        <p>Account Number: 12345678</p>
        <p>Sort Code: 12-34-56</p>
        <p><em>“Give, and it shall be given unto you; good measure, pressed down, and shaken together, and running over, shall men give into your bosom.” - Luke 6:38 KJV</em></p>
      </section>

      {/* Tithes & Offering Section */}
      <section className="giving-section">
        <h2>Tithes & Offering</h2>
        <p>Your tithes and offerings enable the work of the ministry and help us serve our community.</p>
        <p><strong>Bank Account Details:</strong></p>
        <p>Account Name: Tithes & Offering</p>
        <p>Account Number: 87654321</p>
        <p>Sort Code: 65-43-21</p>
        <p><em>“Bring ye all the tithes into the storehouse, that there may be meat in mine house, and prove me now herewith, saith the LORD of hosts, if I will not open you the windows of heaven, and pour you out a blessing, that there shall not be room enough to receive it.” - Malachi 3:10 KJV</em></p>
      </section>

      {/* Fixed Footer Section */}
      <footer className="fixed-footer">
        <p>&copy; {new Date().getFullYear()} Deeper Christian Life Ministry. All Rights Reserved.</p>
      </footer>
    </div>
  );
};

export default Giving;
